import React from 'react';
import paintings from './painting'
import 'semantic-ui-css/semantic.min.css';
// import {Test} from './NavBar'
import NavBar from './NavBar'
import PaintingList from './PaintingList'
import PaintingForm from './PaintingForm'


class App extends React.Component{

  constructor(){
    super()
    // console.log("I am constructor!")
    this.state = {
      list: true,
      paintings: paintings
    }
  }

  changeList = () => {

    this.setState({
      list: !this.state.list
      // temp: "I want this state"
    })

  }

  addPainting = (e) => {
    e.preventDefault()
    console.log(e)
    debugger
  }



  render(){
    // console.log("I am render!")

    return (
    <div>
      <button onClick={this.changeList}>Add Painting</button>

      <NavBar />
      {this.state.list 
      ? <PaintingList paintingObjs={this.state.paintings}/>
      : <PaintingForm add={this.addPainting} />
      }

    </div>
  )};
}

export default App;
